package br.com.casalmoney.app.unauthenticated.view.fragmentsimport android.os.Bundleimport android.util.Logimport androidx.fragment.app.Fragmentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.activity.addCallbackimport androidx.lifecycle.ViewModelProviderimport androidx.navigation.fragment.findNavControllerimport br.com.casalmoney.app.Rimport br.com.casalmoney.app.databinding.FragmentLoginBindingimport br.com.casalmoney.app.unauthenticated.viewmodel.LoginViewModelclass LoginFragment : Fragment() {    private lateinit var binding: FragmentLoginBinding    private val viewModel: LoginViewModel by lazy {        ViewModelProvider(this).get(LoginViewModel::class.java)    }    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        binding = FragmentLoginBinding.inflate(inflater, container, false)        binding.viewModel = viewModel        binding.fragment = this        binding.lifecycleOwner = this        requireActivity().onBackPressedDispatcher.addCallback {            back(binding.root)        }        return binding.root    }    fun login(view: View) {//        viewModel.makeLogin {//            if (it == null) {//        findNavController().navigate(R.id.action_loginFragment_to_authenticatedActivity)//            } else {//                Toast.makeText(context, it.localizedMessage, Toast.LENGTH_LONG).show()//            }//        }    }    fun back(view : View) {        findNavController().popBackStack()    }    fun forgotPassword(view: View){        findNavController().navigate(R.id.action_loginFragment_to_recoverPasswordFragment)    }}